import styled from "styled-components";

export const StyledRegisterComponent = styled.form``;
export const StyledFormField = styled.form.attrs({
  className: "form-group",
})`
  padding: 10px;
  label {
    display: block;
    padding: 10px 0px;
  }
  input {
    padding: 10px;
    width: 100%;
  }
  .required:after {
    content: " *";
  }
`;
