import styled from "styled-components";

export const Card = styled.div`
  display: flex;
  align-items: center;
  box-shadow: 0 4px 4px 0 rgba(0, 0, 0, 0.2);
`;

export const ThumbNail = styled.div`
  img {
    width: 100px;
    height: 100px;
  }
`;
export const Detail = styled.div`
  border-radius: 4px;
  word-wrap: anywhere;
  li {
    list-style-type: none;
    color: gray;
  }
  li:first-child {
    font-size: 24px;
    color: black;
  }
`;
