import { useParams } from "react-router-dom";
import { Container } from "../../components/contaianer";
import { Header } from "../../components/header";
import localStorage from "../../utils/localStorage";
import { Card, Detail, ThumbNail } from "../ListEmployeePage/styles";
import pic from "../../assets/image/pic_50.jpg";
import { loanData } from "./loanData";

interface Props {}
type paramType = {
  id: string;
};
export const DetailEmployee = (props: Props) => {
  const { id } = useParams<paramType>();

  const employee = localStorage.getItem("employee")![parseInt(id) - 1];
  const loanGenData = loanData();
  return (
    <Container>
      <Header />

      {[employee!]?.map((i) => (
        <Card>
          <ThumbNail>
            <img src={pic} alt="profile" />
          </ThumbNail>
          <Detail>
            <ul>
              <li>{i.fullname.toUpperCase()}</li>
              <li>{i.position}</li>
              <li>{i.phoneNumber}</li>
              <li>{i.email}</li>
            </ul>
          </Detail>
        </Card>
      ))}
      <h3>LOAN RECORD</h3>
      {loanGenData.map(({ loans, date }) => {
        return <h5>{date}</h5>;
        {
          loans.map(
            ({
              date,
              borrowed,
              received,
            }: {
              date: Date;
              borrowed: number;
              received: number;
            }) => {
              return (
                <div>
                  {date}
                  {borrowed}
                  {received}
                </div>
              );
            }
          );
        }
      })}
    </Container>
  );
};
