import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Container } from "../../components/contaianer";
import { Header } from "../../components/header";
import { IEmployee } from "../../context";
import localStorage from "../../utils/localStorage";
import { Card, Detail, ThumbNail } from "../ListEmployeePage/styles";
import pic from "../../assets/image/pic_50.jpg";

interface Props {}
type paramType = {
  id: string;
};
export const DetailEmployee = (props: Props) => {
  const { id } = useParams<paramType>();
  const [employee, setemployee] = useState<IEmployee>();

  setemployee(localStorage.getItem("employee")![parseInt(id) - 1]);
  console.log(employee);

  return (
    <Container>
      <Header />
      {console.log(employee)}
      {[employee!]?.map((i) => (
        <Card>
          <ThumbNail>
            <img src={pic} alt="profile" />
          </ThumbNail>
          <Detail>
            <ul>
              <li>{i.fullname.toUpperCase()}</li>
              <li>{i.position}</li>
              <li>{i.phoneNumber}</li>
              <li>{i.email}</li>
            </ul>
          </Detail>
        </Card>
      ))}
    </Container>
  );
};
