import { Header } from "../../components/header";
import React, { Component } from "react";
import { FormValidator } from "../../components/FormValidator";
import { IRule } from "../../utils/validate";
import { ValidationMessage } from "../../components/validateMsg";
import {
  StyledFormField,
  StyledInput,
  StyledRegisterComponent,
} from "./styles";
import { ValidateRegForm } from "./ValidateRegForm";
import { Data, ErrorData } from "./types";

interface Props {}
interface State {
  data: Data;
  errors: ErrorData;
}

export class CreateEmployee extends Component<Props, State> {
  rules: IRule;

  constructor(props: Props) {
    super(props);
    this.state = {
      data: {
        fullname: "",
        email: "",
        username: "",
        password: "",
        confirmPassword: "",
        checked: false,
      },
      errors: {},
    };
    this.rules = {
      fullname: { required: true, minlength: 3, strAlpha: true },
      email: { required: true, email: true },
      username: { required: true, minlength: 3, alphanumeric: true },
      password: { required: true, password: true, equals: "confirmPassword" },
      confirmPassword: { required: true, equals: "password" },
      checked: { checked: true },
    };
  }
  updateFormValue = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    this.setState({ data: { ...this.state.data, [name]: value } });
  };
  setErrorState = (errors: ErrorData) => {
    this.setState({ errors: errors });
  };
  updateFormValueCheck = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = event.target;
    this.setState({ data: { ...this.state.data, [name]: checked } });
  };
  showChecked = (name: keyof ErrorData) => {
    const { data, errors } = this.state;
    return data[name] && !errors[name];
  };

  render() {
    const { data } = this.state;
    return (
      <>
        <FormValidator
          data={data}
          rules={this.rules}
          validateForm={ValidateRegForm}
          setErrorState={this.setErrorState}
        >
          <StyledRegisterComponent>
            <ValidationMessage field="form" />
            <StyledFormField>
              <StyledInput
                placeholder="fullname"
                name="fullname"
                value={data.fullname}
                onChange={this.updateFormValue}
              />
              <ValidationMessage field="fullname" />
            </StyledFormField>

            <StyledFormField>
              <StyledInput
                name="email"
                placeholder="email"
                value={data.email}
                onChange={this.updateFormValue}
              />

              <ValidationMessage field="email" />
            </StyledFormField>

            <StyledFormField>
              <StyledInput
                name="username"
                placeholder={"username"}
                value={data.username}
                onChange={this.updateFormValue}
                autoComplete={"username"}
              />

              <ValidationMessage field="username" />
            </StyledFormField>

            <StyledFormField>
              <StyledInput
                type="password"
                name="password"
                placeholder="password"
                value={data.password}
                onChange={this.updateFormValue}
                autoComplete={"new-password"}
              />
              <ValidationMessage field="password" />
            </StyledFormField>

            <StyledFormField>
              <StyledInput
                type="password"
                name="confirmPassword"
                placeholder="confirm password"
                value={data.confirmPassword}
                onChange={this.updateFormValue}
                autoComplete={"new-password"}
              />
              <ValidationMessage field="confirmPassword" />
            </StyledFormField>
          </StyledRegisterComponent>
        </FormValidator>
      </>
    );
  }
}
