import { createContext } from "react";
export interface IUser {
  id?: number;
  fullname: string;
  username: string;
  password: string;
  token?: string;
}
export interface IFormContext {
  submitForm: (user: IUser) => Promise<void | string>;
  loading: boolean;
  message: string;
}
export const FormCtx = createContext<IFormContext>({
  submitForm: (_user) => new Promise(() => {}),
  loading: false,
  message: "",
});
